//SPDX-License-Identifier: MIT
pragma solidity >= 0.6.0 <0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/EnumerableSet.sol";
import {CollectibleUtils as ut} from "./utils.sol";

/**
 * @title Mining NFT tokens and introducing rarity
 * @author Robbe Louwet
 * 

 a tier is unique as well as its mask
 a tier's mask is the amount of trailing 1's, it signifies the odds of mining that tier
 e.g. mask of 111 (or "3" -> 2^3 - 1) tells that there is a 1/7 chance to mine this tier (!= odds to mine an item in this tier)
 tier             => challenge mask               -> odds

 "normal"         => ?                            -> 93% (1 - all other odds)
 "common"         => 0xF      OR           1111   -> 1/15
 "uncommon"       => 0x1F     OR         1 1111   -> 1/31
 "Legendary"      => 0x3F     OR       111 1111   -> 1/127
 "Exotic"         => 0xFF     OR      1111 1111   -> 1/255
 */
contract Collectible is ERC721 {

    address distributor;

    event newDistributor(address indexed from, address to);

    // tier_id aka tier mask => Tier
    mapping(uint => ut.Tier) tiers;
    uint[] tier_ids; // [2, 3, ...], means tiers with mask [11, 111, ...]

    // contains all not yet mined items in circulation.
    // trailing mask bits of the key (item_id) signify the tier
    //
    // item_id => Item
    mapping(uint => ut.Item) items;

    // structs can't yet contain an array
    // tier_id => ItemBlueprint[]
    mapping(uint => ut.ItemBlueprint[]) tier_blueprints;

    modifier isDistributor() {
        require(msg.sender == distributor);
        _;
    }

    constructor() ERC721("Collectible", "CLB"){
        distributor = msg.sender;

        // it's cheaper to store the exponent than to later on calculate expensive log2(x) operations
        addTier("normal", 3); //the challenge mask is 2^3-1
        addTier("common", 4);
        addTier("uncommon", 5);
        addTier("Legendary", 7);
        addTier("Exotic", 8);

        addTierBlueprint(3, 5, "Sword");
        addTierBlueprint(3, 5, "Pickaxe");
        addTierBlueprint(3, 5, "Axe");

    }

    function getItemName(uint blueprint_id, uint tier_id) public view returns (string memory) {
        require(tiers[tier_id].id != 0, "This tier doesn't exist!");
        require(tier_blueprints[tier_id].length >= blueprint_id, "There is no such blueprint in this tier!");

        return tier_blueprints[tier_id][blueprint_id].name;
    }

    /**
     * the total oods will be 1 out of 2^(exponent) - 1
     * so if exponent is 3, the total odds of mining this tier is 2^3 - 1 = 1 out of 7
     */
    function newTier(string memory tier, uint exponent) public isDistributor {
        addTier(tier, exponent);
    }

    function addTier(string memory tier, uint id) private returns (uint) {
        require(id >0 && id < 256, "Please specify a number between 0 and 256");

        require(tiers[id].id == 0, "This tier exists and already has a mask.");
        //require(!ut.contains_string(tier_names, tier), "Tier is already a known tier.");

        //require(keccak256(abi.encodePacked(dd_tier[id])) == keccak256(abi.encodePacked("")), "This rarity tier already exists");
        require(contains_int(tier_ids, id) == false, "This is already a known rarity value");

        ut.Tier memory new_tier;
        new_tier.name = tier;
        new_tier.id = id;

        tiers[id] = new_tier;
        tier_ids.push(id);

        return id;
    }

    function addTierBlueprint(uint tier_id, uint max_supply, string memory name) public isDistributor{
        require(tiers[tier_id].id != 0, "Adding BluePrint to nonexistent tier!");
        ut.ItemBlueprint memory bp = ut.ItemBlueprint(max_supply, name);
        tier_blueprints[tier_id].push(bp);
    }

    function getTierItemsCount(uint tier_id) public returns (uint){
        require(tiers[tier_id].id != 0, "This tier doesn't exist!");
        return tier_blueprints[tier_id].length;
    }

    function getMasks() public view returns (uint[] memory) {
        return tier_ids;
    }

    function mint(uint id, address to) public isDistributor {
        _safeMint(to, id);
    }

    function transferDistributorRole(address to) public isDistributor {
        distributor = to;
        emit newDistributor(msg.sender, distributor);
    }

    function contains_string(string[] storage array, string memory target) private view returns(bool) {
        for (uint256 index = 0; index < array.length; index++) {
            if (keccak256(abi.encodePacked(array[index])) == keccak256(abi.encodePacked(target))) {
                return true;
            }
        }
        return false;
    }

    function contains_int(uint[] storage array, uint target) private view returns(bool) {
        bool found;
        for (uint256 index = 0; index < array.length; index++) {
            if (array[index] == target) {
                found = true;
                break;
            }
        }
    }
}
