//SPDX-License-Identifier: MIT
pragma solidity >= 0.6.0 <0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

/**
 * @title Mining NFT tokens and introducing rarity
 * @author Robbe Louwet
 * 
 */
contract Diamond is ERC721 {

    constructor() ERC721("Diamond", "DMD"){
    }

    /**
     * @param challenge is the challenge string, basically a random input of arbitrary length
     * @param target an identifier, this is what we're trying to brute force, the idea is that this is a short string, e.g.: 'DIAM'
     * that the hash should end with, and which is unique to the actual NFT token.
     */
    function mine(string memory challenge, string memory target) public returns(bool) {
        bytes32 output_hash = keccak256(bytes(challenge));

        bytes memory hash_bytes = toBytes(output_hash);
        bytes memory target_bytes = bytes(target);

        require(target_bytes.length <= hash_bytes.length, "Error, the target identifier is longer than the hash.");

        bool failed;
        for (uint256 i = 0; i < target_bytes.length; i++) {
            bytes1 hash_byte = hash_bytes[hash_bytes.length-i];
            bytes1 target_byte = target_bytes[target_bytes.length-i];

            if (hash_byte != target_byte) failed=true;
            if (failed) break;
        }

        return !failed;
    }

    function toBytes(bytes32 _data) public pure returns (bytes memory) {
        return abi.encodePacked(_data);
  }
}

