//SPDX-License-Identifier: MIT
pragma solidity >= 0.6.0 <0.8.0;

import "./Collectible.sol";
import {CollectibleUtils as ut} from "./utils.sol";

contract Distributor {
    Collectible private token;

    constructor (Collectible _token) {
        token=_token;
    }

    event minedSuccessfully(address indexed user, string tier, string item_name, string challenge);

    /**
     * @param challenge is the challenge string, basically a random input of arbitrary length
     * 
     * summary:
     * challenge = "apple"
     * target = "cd25"
     * returns keccak256("apple") == 0x??...??cd25 (so returns true)
     */
    function check_challenge(string memory challenge) public returns(uint) {

        // the combined input to hash. Should be challenge string and latest block hash
        bytes memory input = abi.encodePacked(challenge/*,blockhash(block.number-1)*/);

        // hash of the challenge input
        bytes32 hashed_challenge = keccak256(input);

        // detect if we match a tier, and hich one
        uint tier = find_present_tier(hashed_challenge); // returns mask_bits
        // tier can't be empty string
        require(tier != 0, "No tier found");

        // we know the tier, now figure out which item they mined
        uint item_id = which_item(hashed_challenge, tier);

        //string memory item_name = token.getItemName(item_id);

        //emit minedSuccessfully(msg.sender, tier, item_name, challenge);

        // we keep the challenge hash as id, but convert to int
        //uint challenge_id;
        //assembly{challenge_id := shl(0x0, hashed_challenge)}   

        return 0;//challenge_id;
    }

    function mine(string memory challenge) public payable {
        uint token_id = check_challenge(challenge);
        require(token_id == 0x0, "Did not crack the challenge");
        //token.mint(token_id, msg.sender);
    }

    /**
     * find out if the hash covers one of the tier masks, if so, return the tier name string.
     */
    function find_present_tier(bytes32 _hash) public view returns (uint){
        uint[] memory mask_exps = token.getMasks();
        for (uint256 index = 0; index < mask_exps.length; index++) {
            uint mask = 2**mask_exps[index]-1;
            uint offset = 256 - mask_exps[index];
            bytes32 xored = check_mask(_hash, mask, offset);

            if (xored == 0x0) return mask_exps[index];
        }
        return 0;
    }

    // remeber, item_id INCLUDES the tier's mask
    function which_item(bytes32 _hash, uint tier) private returns (uint){
        uint size = token.getTierItemsCount(tier)+1; // returns 0-based length

        // convert the hash to an int, and shift the mask bits outside the buffer
        uint _hash_int;
        assembly {
            _hash_int := shr(tier, _hash)
        }

        // if we now calculate the hash % amount_items, we should result in a random, existing, item_id
        uint blueprint_id = _hash_int % size;
        return blueprint_id;
    }

    // check if we have a match
    // if we do, by shifting the mask all the way to the start of the reserved 32 byte memory (and shift the hash by the same offset)
    // if we xor those 2 and we result in 0, then the target mask matches the last x bits of the hashed_challenge, and the sender has 'won'
    function check_mask(bytes32 _hash, uint mask, uint mask_length) private pure returns (bytes32 xored) {
        assembly{
            let x := shl(mask_length, _hash)
            let y := shl(mask_length, mask)
            xored := xor(x, y)
        }
    }

    /**
     * Takes the sub-bitstring specified by start- and end_index, shifts it all the way to the right, and returns it.
     */
    function get_sub_bit_array(bytes32 bit_string, uint start_index, uint end_index) private pure returns (bytes32 ret) {
        uint offset_left = start_index;
        // first shift back to original spot, now shift all the way to the right
        uint second_offset_right = offset_left + (256-end_index);
        assembly {
            let x := shl(offset_left, bit_string)
            ret := shr(second_offset_right, x)
        }
        return ret;
    }
}

